import React, { useState, useEffect } from 'react';
import { askGemini } from '../services/aiService';

const AssistantPage = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  // ✅ Tin nhắn hệ thống cố định
  const systemMessage = {
    role: 'user',
    parts: [
      {
        text:
          'Bạn là một trợ lý ảo chuyên về du lịch. Hãy luôn tập trung vào việc gợi ý lịch trình, tư vấn điểm đến, ngân sách và trải nghiệm du lịch. Không trả lời các câu hỏi không liên quan đến du lịch.'
      }
    ]
  };

  const handleSend = async () => {
    if (!input.trim() || loading) return;

    const newUserMessage = { role: 'user', parts: [{ text: input }] };

    const allMessages = [...messages, newUserMessage];
    setMessages(allMessages);
    setInput('');
    setLoading(true);

    try {
      // ✅ Gửi system message + tất cả các message còn lại
      const reply = await askGemini([systemMessage, ...allMessages]);
      const replyMessage = {
        role: 'model',
        parts: [{ text: reply }]
      };
      setMessages((prev) => [...prev, replyMessage]);
    } catch (err) {
      console.error(err);
      setMessages((prev) => [
        ...prev,
        {
          role: 'model',
          parts: [{ text: '❌ Lỗi kết nối đến AI' }]
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const renderMessageText = (msg) => {
    if (msg.parts && msg.parts.length > 0) return msg.parts[0].text;
    if (msg.text) return msg.text;
    return '';
  };

  return (
    <div className="p-4">
      <div className="h-[70vh] overflow-y-auto bg-gray-100 p-2 rounded">
        {messages.map((m, i) => (
          <div
            key={i}
            className={`mb-2 ${m.role === 'user' ? 'text-right' : 'text-left'}`}
          >
            <span className="inline-block bg-white shadow p-2 rounded">
              {renderMessageText(m)}
            </span>
          </div>
        ))}
        {loading && (
          <div className="text-left mb-2">
            <span className="inline-block bg-white shadow p-2 rounded">
              Đang nghĩ...
            </span>
          </div>
        )}
      </div>

      <div className="mt-4 flex">
        <input
          className="flex-grow p-2 border rounded"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSend()}
          disabled={loading}
          placeholder="Nhập câu hỏi du lịch của bạn..."
        />
        <button
          onClick={handleSend}
          className="ml-2 px-4 py-2 bg-blue-500 text-white rounded"
          disabled={loading}
        >
          Gửi
        </button>
      </div>
    </div>
  );
};

export default AssistantPage;
