// src/components/MapRoute.jsx
import React, { useEffect } from 'react';
import { MapContainer, TileLayer } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine';
import 'leaflet-routing-machine/dist/leaflet-routing-machine.css';

const MapRoute = ({ places }) => {
  useEffect(() => {
    if (!places || places.length < 2) return;

    const map = L.map('leaflet-map').setView([16.0471, 108.2062], 7);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '&copy; OpenStreetMap',
    }).addTo(map);

    L.Routing.control({
      waypoints: places.map((place) => L.latLng(0, 0)), // sẽ cập nhật ở bước sau
      router: L.Routing.osrmv1({
        serviceUrl: `https://router.project-osrm.org/route/v1`,
      }),
      geocoder: L.Control.Geocoder.nominatim(),
    }).addTo(map);
  }, [places]);

  return <div id="leaflet-map" style={{ height: '500px' }} />;
};

export default MapRoute;
