import React, { useEffect, useRef } from 'react';

const MapRoute = ({ places }) => {
  const mapRef = useRef(null);

  useEffect(() => {
    if (!window.google || !places || places.length < 2) return;

    const map = new window.google.maps.Map(mapRef.current, {
      zoom: 6,
      center: { lat: 16.0471, lng: 108.2062 }, // Trung tâm VN
    });

    const directionsService = new window.google.maps.DirectionsService();
    const directionsRenderer = new window.google.maps.DirectionsRenderer({ map });

    const waypoints = places.slice(1, -1).map((place) => ({
      location: place,
      stopover: true,
    }));

    directionsService.route(
      {
        origin: places[0],
        destination: places[places.length - 1],
        waypoints,
        travelMode: window.google.maps.TravelMode.DRIVING,
      },
      (result, status) => {
        if (status === window.google.maps.DirectionsStatus.OK) {
          directionsRenderer.setDirections(result);
        } else {
          console.error('Lỗi khi vẽ bản đồ:', status);
        }
      }
    );
  }, [places]);

  return <div ref={mapRef} className="w-full h-[400px] rounded shadow border" />;
};

export default MapRoute;
