import React, { useState } from "react";
import "../styles/Login.css";
import backgroundImage from "../assets/background.jpg"; // Thay ảnh phù hợp

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Email:", email, "Password:", password);
  };

  return (
    <div className="login-container">
      {/* Phần bên trái chứa hình ảnh và câu quote */}
      <div className="login-left">
        <img src={backgroundImage} alt="Travel" className="login-image" />
        <div className="quote">
          <h2>TRAVEL IS THE ONLY THING YOU BUY THAT MAKES YOU RICHER</h2>
        </div>
      </div>

      {/* Phần bên phải chứa form login */}
      <div className="login-right">
        <h2 className="logo">TRAVEL BLOGGER</h2>

        <div className="social-icons">
          <i className="fab fa-facebook-f"></i>
          <i className="fab fa-google"></i>
          <i className="fab fa-linkedin-in"></i>
        </div>
        <p>or use your email account</p>

        <form className="login-form" onSubmit={handleSubmit}>
          <div className="input-group">
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <i className="fas fa-user"></i>
          </div>

          <div className="input-group">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <i className="fas fa-eye"></i>
          </div>

          <a href="#" className="forgot-password">Forgot Your Password?</a>
          <button type="submit" className="login-btn">ENTER</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
