// Home.tsx
import React, { useState, useEffect, useRef } from 'react';
import '../styles/home.css';
import logo from '../assets/logo.png';
import { askGemini } from '../services/aiService';

const Home: React.FC = () => {
  const [messages, setMessages] = useState<any[]>([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [chatVisible, setChatVisible] = useState(false);
  const chatEndRef = useRef<HTMLDivElement>(null);
  const inputStartRef = useRef<HTMLTextAreaElement>(null);
  const inputChatRef = useRef<HTMLTextAreaElement>(null);

  const handleSend = async () => {
    if (!input.trim() || loading) return;

    const userMessage = { role: 'user', parts: [{ text: input }] };
    const systemPrompt = {
      role: 'user',
      parts: [
        {
          text: 'B·∫°n l√† m·ªôt tr·ª£ l√Ω ·∫£o chuy√™n v·ªÅ du l·ªãch t√™n l√† chipchip. Ch·ªâ tr·∫£ l·ªùi c√°c c√¢u h·ªèi li√™n quan ƒë·∫øn th√¥ng tin kh√°ch h√†ng v√† du l·ªãch nh∆∞ g·ª£i √Ω h√†nh tr√¨nh, ƒë·ªãa ƒëi·ªÉm tham quan, ng√¢n s√°ch, ph∆∞∆°ng ti·ªán di chuy·ªÉn, tr·∫£i nghi·ªám. N·∫øu c√¢u h·ªèi kh√¥ng li√™n quan, h√£y t·ª´ ch·ªëi m·ªôt c√°ch l·ªãch s·ª±. Kh√¥ng h·ªèi nhi·ªÅu h∆°n 1 c√¢u. Th√™m icon cu·ªëi c√¢u'
        }
      ]
    };

    const conversation = [systemPrompt, ...messages, userMessage];
    setMessages((prev) => [...prev, userMessage]);
    setInput('');
    setLoading(true);

    if (!chatVisible) {
      setChatVisible(true);
      setTimeout(() => inputChatRef.current?.focus(), 100);
    } else {
      inputChatRef.current?.focus();
    }

    try {
      const reply = await askGemini(conversation);
      const botMessage = { role: 'model', parts: [{ text: reply }] };
      setMessages((prev) => [...prev, botMessage]);
    } catch (err) {
      console.error(err);
      setMessages((prev) => [
        ...prev,
        { role: 'model', parts: [{ text: '‚ùå L·ªói k·∫øt n·ªëi ƒë·∫øn AI' }] }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const renderText = (msg: any) => msg.parts?.[0]?.text || msg.text || '';

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, loading]);

  return (
    <div className="home">
      <img src={logo} alt="CheapTrip Background Logo" className="background-logo" />

      <div className="content">
        <h1 className="text-4xl font-bold text-white mb-4">Ch√†o m·ª´ng ƒë·∫øn v·ªõi CheapTrip üåç</h1>
        <p className="text-lg text-gray-200 mb-6">Tr√≤ chuy·ªán v·ªõi ChipChip ƒë·ªÉ l√™n l·ªãch tr√¨nh du l·ªãch üß≥</p>
      </div>

      {!chatVisible && (
        <div className="chat-input-container beautiful start-chat">
          <textarea
            ref={inputStartRef}
            className="chat-input beautiful"
            placeholder="H·ªèi ChipChip v·ªÅ chuy·∫øn ƒëi ti·∫øp theo c·ªßa b·∫°n..."
            rows={1}
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                handleSend();
              }
            }}
            disabled={loading}
          />
          <button className="send-button beautiful" onClick={handleSend} disabled={loading}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
              <path
                d="M12 4L20 12L12 20L10.5 18.5L16 13H4V11H16L10.5 5.5L12 4Z"
                fill="currentColor"
              />
            </svg>
          </button>
        </div>
      )}

      {chatVisible && (
        <div className="chat-box beautiful">
          <div className="chat-history">
            {messages.map((m, i) => (
              <div
                key={i}
                className={`chat-message ${m.role === 'user' ? 'user' : 'bot'}`}
              >
                <div className="message-content">{renderText(m)}</div>
              </div>
            ))}
            {loading && (
              <div className="chat-message bot">
                <div className="message-content">ƒêang nghƒ©...</div>
              </div>
            )}
            <div ref={chatEndRef} />
          </div>

          <div className="chat-input-container beautiful">
            <textarea
              ref={inputChatRef}
              className="chat-input beautiful"
              placeholder="H·ªèi ChipChip v·ªÅ chuy·∫øn ƒëi ti·∫øp theo c·ªßa b·∫°n..."
              rows={1}
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  handleSend();
                }
              }}
              disabled={loading}
            />
            <button className="send-button beautiful" onClick={handleSend} disabled={loading}>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                <path
                  d="M12 4L20 12L12 20L10.5 18.5L16 13H4V11H16L10.5 5.5L12 4Z"
                  fill="currentColor"
                />
              </svg>
            </button>
          </div>
        </div>
      )}

      <div className="scroll-indicator">
        <svg width="24" height="24" fill="#fff">
          <path
            d="M12 5V19M12 19L5 12M12 19L19 12"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </div>
    </div>
  );
};

export default Home;
