// src/components/MapRoute.jsx
import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Polyline, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

const MapRoute = ({ places }) => {
  const [coordinates, setCoordinates] = useState([]);

  useEffect(() => {
    const fetchCoords = async () => {
      const coords = await Promise.all(
        places.map(async (place) => {
          const res = await fetch(
            `https://nominatim.openstreetmap.org/search?format=json&q=${place}`
          );
          const data = await res.json();
          if (data.length > 0) {
            const { lat, lon } = data[0];
            return [parseFloat(lat), parseFloat(lon)];
          }
          return null;
        })
      );
      setCoordinates(coords.filter(Boolean));
    };

    if (places.length >= 2) fetchCoords();
  }, [places]);

  return (
    <div style={{ height: '400px', width: '100%' }}>
      <MapContainer center={[16.0471, 108.2062]} zoom={7} style={{ height: '100%', width: '100%' }}>
        <TileLayer
          attribution='&copy; OpenStreetMap contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {coordinates.map((pos, idx) => (
          <Marker key={idx} position={pos}>
            <Popup>{places[idx]}</Popup>
          </Marker>
        ))}
        {coordinates.length >= 2 && <Polyline positions={coordinates} color="blue" />}
      </MapContainer>
    </div>
  );
};

export default MapRoute;
