import React, { useState } from 'react';
import "../styles/header.css";
import "../styles/Login.css"; // Import Login.css để tái sử dụng kiểu
import logo from '../assets/logo.png';
import backgroundImage from '../assets/background.png'; // Hình ảnh nền cho modal

const Header: React.FC = () => {
  const [isLoginModalOpen, setIsLoginModalOpen] = useState<boolean>(false); // State để quản lý modal
  const [email, setEmail] = useState<string>("");
  const [password, setPassword] = useState<string>("");

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>): void => {
    e.preventDefault();
    console.log("Email:", email, "Password:", password);
    setIsLoginModalOpen(false); // Đóng modal sau khi đăng nhập
  };

  return (
    <>
      <header className="header">
        <div className="logo">
          <img src={logo} alt="Logo" />
        </div>
        
        <nav className="auth-links">
          <a href="/register">Đăng ký</a>
          <span className="login" onClick={() => setIsLoginModalOpen(true)}>
            Đăng nhập
          </span>
        </nav>
      </header>

      {/* Modal đăng nhập */}
      {isLoginModalOpen && (
        <div className="modal-overlay">
          <div className="login-container">
            <div className="login-left">
              <img src={backgroundImage} alt="Travel" className="login-image" />
              <div className="quote">
                <h2>“CheapTrip – Gà mà biết đi chơi!”</h2>
              </div>
            </div>

            <div className="login-right">
              <h2 className="logo">Cheap Trip</h2>
              <div className="social-icons">
                <i className="fab fa-facebook-f"></i>
                <i className="fab fa-google"></i>
                <i className="fab fa-linkedin-in"></i>
              </div>
              <p>đăng nhập bằng email</p>

              <form className="login-form" onSubmit={handleSubmit}>
                <div className="input-group">
                  <input
                    type="email"
                    placeholder="Tên đăng nhập"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                  <i className="fas fa-user"></i>
                </div>

                <div className="input-group">
                  <input
                    type="password"
                    placeholder="Mật khẩu"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                  <i className="fas fa-eye"></i>
                </div>

                <a href="#" className="forgot-password">Quên mật khẩu?</a>
                <button type="submit" className="login-btn">ĐĂNG NHẬP</button>
              </form>
              <button
                className="close-btn"
                onClick={() => setIsLoginModalOpen(false)}
              >
                Đóng
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default Header;
