import React, { useState } from 'react';
import { askGemini } from '../services/aiService';
import MapRoute from '../components/MapRoute';

const AssistantPage = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [places, setPlaces] = useState([]);

  const handleSend = async () => {
    if (!input.trim() || loading) return;

    const userMessage = { role: 'user', parts: [{ text: input }] };

    const systemPrompt = {
      role: 'user',
      parts: [{
        text: 'Bạn là một trợ lý ảo chuyên về du lịch tên là ChipChip. Chỉ hỏi 1 câu mỗi lượt. Chỉ trả lời về lịch trình, địa điểm, ngân sách du lịch. Nếu người dùng hỏi chủ đề khác, từ chối nhẹ nhàng. Thêm icon vào cuối câu.'
      }]
    };

    const conversation = [systemPrompt, ...messages, userMessage];

    setMessages((prev) => [...prev, userMessage]);
    setInput('');
    setLoading(true);

    try {
      const reply = await askGemini(conversation);
      const botMessage = { role: 'model', parts: [{ text: reply }] };
      setMessages((prev) => [...prev, botMessage]);

      // 🚀 Trích xuất địa điểm từ nội dung AI
      const extracted = extractLocations(reply);
      if (extracted.length >= 2) setPlaces(extracted);
    } catch (err) {
      console.error(err);
      setMessages((prev) => [...prev, {
        role: 'model',
        parts: [{ text: '❌ Lỗi kết nối đến AI' }]
      }]);
    } finally {
      setLoading(false);
    }
  };
  const extractLocations = (text) => {
    const regex = /(?:đến|tham quan|ở|ghé|thăm)\s([A-ZĐ][a-zA-Z\s,]+)/g;
    const matches = [...text.matchAll(regex)]
      .map((m) => m[1].split(','))
      .flat();
    return matches
      .map((s) => s.trim())
      .filter((s, i, arr) => s && arr.indexOf(s) === i);
  };
  

  const renderText = (msg) =>
    msg.parts?.[0]?.text || msg.text || '';

  return (
    <div className="p-4">
      <div className="h-[70vh] overflow-y-auto bg-gray-100 p-2 rounded">
        {messages.map((m, i) => (
          <div
            key={i}
            className={`mb-2 ${m.role === 'user' ? 'text-right' : 'text-left'}`}
          >
            <span className="inline-block bg-white shadow p-2 rounded">
              {renderText(m)}
            </span>
          </div>
        ))}
        {loading && (
          <div className="text-left mb-2">
            <span className="inline-block bg-white shadow p-2 rounded">
              Đang nghĩ...
            </span>
          </div>
        )}
      </div>

      {places.length >= 2 && (
        <div className="mt-4">
          <h2 className="font-semibold mb-2">Tuyến đường gợi ý</h2>
          <MapRoute places={places} />
        </div>
      )}

      <div className="mt-4 flex">
        <input
          className="flex-grow p-2 border rounded"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSend()}
          disabled={loading}
          placeholder="Hỏi ChipChip về chuyến đi của bạn..."
        />
        <button
          onClick={handleSend}
          className="ml-2 px-4 py-2 bg-blue-500 text-white rounded"
          disabled={loading}
        >
          Gửi
        </button>
      </div>
    </div>
  );
};

export default AssistantPage;
