  const [isExpanded, setIsExpanded] = useState(false);

  const handleSend = () => {
    if (!input.trim()) return;
    setMessages((prev) => [...prev, input]);
    setInput('');
    setIsExpanded(true); // M·ªü r·ªông chat box khi g·ª≠i
  };

  return (
    <div className="home">
