          <div className="chat-messages">
            {messages.map((msg, idx) => (
              <div key={idx} className="chat-message">
                <span>{msg}</span>
              </div>
            ))}
          </div>
        )}

        {/* Thanh nh·∫≠p li·ªáu */}
        <div className="chat-input-bar">
          <input
            type="text"
            placeholder="H·ªèi b·∫•t k·ª≥ ƒëi·ªÅu g√¨ v·ªÅ chuy·∫øn ƒëi..."
            value={input}
