import React, { useState } from 'react';
import { askGemini } from '../services/aiService';

const AssistantPage = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = async () => {
    if (!input.trim()) return;
  
    const newMessages = [...messages, { role: 'user', text: input }];
    setMessages(newMessages);
    setInput('');
  
    try {
      const reply = await askGemini(newMessages); // ✅ gửi toàn bộ history
      setMessages([...newMessages, { role: 'ai', text: reply }]);
    } catch (err) {
      console.error(err);
      setMessages([...newMessages, { role: 'ai', text: '❌ Lỗi kết nối đến AI' }]);
    }
  };
  

  return (
    <div className="p-4">
      <div className="h-[70vh] overflow-y-auto bg-gray-100 p-2 rounded">
        {messages.map((m, i) => (
          <div key={i} className={`mb-2 ${m.role === 'user' ? 'text-right' : 'text-left'}`}>
            <span className="inline-block bg-white shadow p-2 rounded">{m.text}</span>
          </div>
        ))}
      </div>
      <div className="mt-4 flex">
        <input
          className="flex-grow p-2 border rounded"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSend()}
        />
        <button onClick={handleSend} className="ml-2 px-4 py-2 bg-blue-500 text-white rounded">
          Gửi
        </button>
      </div>
    </div>
  );
};

export default AssistantPage;
