import React, { useState, useRef, useEffect } from 'react';
import '../styles/home.css';
import logo from '../assets/logo.png';
import { askGemini } from '../services/aiService';

interface Message {
  role: 'user' | 'model';
  parts: { text: string }[];
}

const Home: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, loading]);

  const handleSend = async () => {
    if (!input.trim() || loading) return;

    const userMessage: Message = {
      role: 'user',
      parts: [{ text: input }],
    };

    const systemPrompt: Message = {
      role: 'user',
      parts: [{
        text: 'Bạn là một trợ lý ảo chuyên về du lịch tên là chipchip. Chỉ trả lời các câu hỏi liên quan đến hành trình, địa điểm, chi phí, trải nghiệm du lịch. Không hỏi nhiều hơn 1 câu. Nếu không liên quan, hãy từ chối lịch sự. Thêm icon cuối câu.',
      }],
    };

    const conversation: Message[] = [systemPrompt, ...messages, userMessage];

    setMessages((prev) => [...prev, userMessage]);
    setInput('');
    setLoading(true);

    try {
      const reply = await askGemini(conversation);
      const botMessage: Message = {
        role: 'model',
        parts: [{ text: reply }],
      };
      setMessages((prev) => [...prev, botMessage]);
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        {
          role: 'model',
          parts: [{ text: '❌ Lỗi kết nối đến AI' }],
        },
      ]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="chatgpt-home">
      {/* Background logo */}
      <img src={logo} alt="CheapTrip Logo" className="background-logo" />

      {/* Chat area */}
      <div className="chat-window">
        <div className="chat-messages">
          {messages.map((msg, idx) => (
            <div key={idx} className={`chat-message ${msg.role}`}>
              <div className="message-bubble">{msg.parts[0]?.text}</div>
            </div>
          ))}
          {loading && (
            <div className="chat-message model">
              <div className="message-bubble">ChipChip đang trả lời...</div>
            </div>
          )}
          <div ref={chatEndRef} />
        </div>

        {/* Input */}
        <div className="chat-input">
          <input
            type="text"
            placeholder="Nhập câu hỏi của bạn về chuyến đi..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleSend()}
            disabled={loading}
          />
          <button onClick={handleSend} disabled={loading}>Gửi</button>
        </div>
      </div>
    </div>
  );
};

export default Home;
