import React, { useState, useEffect } from 'react';
import '../styles/TripHistory.css';

interface Trip {
  id: number;
  name: string;
  startDate: string;
  endDate: string;
  location: string;
  places: { name: string; lat: number; lng: number }[];  // List of places with coordinates
  cost: number;
}

const TripHistory: React.FC = () => {
  const [trips, setTrips] = useState<Trip[]>([]); // State to store list of trips
  const [selectedTrip, setSelectedTrip] = useState<Trip | null>(null); // State to store the selected trip

  useEffect(() => {
    // Fetch trips from backend API (you can replace this with actual API call)
    const fetchTrips = async () => {
      const response = await fetch('/api/trips'); // Placeholder for your API endpoint
      const data = await response.json();
      setTrips(data);
    };

    fetchTrips();
  }, []);

  const handleTripSelect = (trip: Trip) => {
    setSelectedTrip(trip);
  };

  return (
    <div className="trip-history-page">
      <h1>Lịch Sử Chuyến Đi</h1>
      <p>Đây là danh sách các lịch trình chuyến đi bạn đã tạo với CheapTrip.</p>

      <div className="trip-history-container">
        <div className="trip-list">
          {trips.map((trip) => (
            <div className="trip-item" key={trip.id} onClick={() => handleTripSelect(trip)}>
              <h3>{trip.name}</h3>
              <p>Ngày: {trip.startDate} - {trip.endDate}</p>
              <p>Điểm đến: {trip.location}</p>
            </div>
          ))}
        </div>

        {selectedTrip && (
          <div className="trip-details">
            <h2>Chi tiết chuyến đi: {selectedTrip.name}</h2>
            <p>Ngày: {selectedTrip.startDate} - {selectedTrip.endDate}</p>
            <p>Điểm đến: {selectedTrip.location}</p>
            <p>Số tiền đã chi: {selectedTrip.cost} VND</p>

            {/* Google Map component */}
            <div className="google-map">
              {/* You can use the Google Maps API or a map library like react-google-maps */}
              <iframe
                title="Trip Map"
                width="100%"
                height="400px"
                src={`https://www.google.com/maps/embed/v1/place?key=AIzaSyBONHNPOZQkSCXMEnNcvCIPn5dq5t1pIS8&q=${selectedTrip.places.map(place => `${place.lat},${place.lng}`).join('|')}`}
                frameBorder="0"
                allowFullScreen
              ></iframe>
            </div>
          </div>
        )}
      </div>

      <a href="/">Quay lại trang chủ</a>
    </div>
  );
};

export default TripHistory;
