import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import '../styles/TripHistory.css';

interface Trip {
  id: number;
  name: string;
  startDate: string;
  endDate: string;
  location: string;
  places: { name: string; lat: number; lng: number }[];
  cost: number;
}

interface Expense {
  category: string;
  amount: number;
  description: string;
}

interface WeatherForecast {
  forecastDate: string;
  temperature: number;
  weatherCondition: string;
}

const TripHistory: React.FC = () => {
  const [trips, setTrips] = useState<Trip[]>([]);
  const [selectedTrip, setSelectedTrip] = useState<Trip | null>(null);
  const [expenses, setExpenses] = useState<Expense[]>([]);
  const [weatherForecasts, setWeatherForecasts] = useState<WeatherForecast[]>([]);

  const serverUrl = "http://localhost:5000"; // Backend server URL

  // Get the token (from localStorage, sessionStorage, or context)
  const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6MSwiZW1haWwiOiJ1c2VyQGV4YW1wbGUuY29tIiwiaWF0IjoxNzQzODY0NTU5LCJleHAiOjE3NDM4NjgxNTl9.7EzulcggAnFChhYI61KqJTgaWc_liwjZfwfdEMtYU5w"; // Adjust based on where you store the token

  useEffect(() => {
    // Fetch trips data from the backend
    const fetchTrips = async () => {
      try {
        const response = await fetch(`${serverUrl}/api/trips`, {
          headers: {
            'Authorization': `Bearer ${token}`, // Include the token in the request header
          },
        });
        const data = await response.json();
        setTrips(data);
      } catch (error) {
        console.error('Error fetching trips:', error);
      }
    };

    fetchTrips();
  }, [token]);

  useEffect(() => {
    // Fetch expenses and weather forecasts when a trip is selected
    const fetchDetails = async (tripId: number) => {
      try {
        // Fetch expenses for the selected trip
        const expenseResponse = await fetch(`${serverUrl}/api/trips/${tripId}/expenses`, {
          headers: {
            'Authorization': `Bearer ${token}`, // Include the token in the request header
          },
        });
        const expenseData = await expenseResponse.json();
        setExpenses(expenseData);

        // Fetch weather forecasts for the selected trip
        const weatherResponse = await fetch(`${serverUrl}/api/trips/${tripId}/weather-forecasts`, {
          headers: {
            'Authorization': `Bearer ${token}`, // Include the token in the request header
          },
        });
        const weatherData = await weatherResponse.json();
        setWeatherForecasts(weatherData);
      } catch (error) {
        console.error('Error fetching trip details:', error);
      }
    };

    if (selectedTrip) {
      fetchDetails(selectedTrip.id);
    }
  }, [selectedTrip, token]);

  return (
    <div className="trip-history-container">
      {/* Danh sách chuyến đi (Bên trái) */}
      <div className="trip-list">
        <h2>Danh Sách Chuyến Đi</h2>
        {trips.map((trip) => (
          <div key={trip.id} className="trip-item" onClick={() => setSelectedTrip(trip)}>
            <h3>{trip.name}</h3>
            <p>{trip.startDate} - {trip.endDate}</p>
            <p>{trip.location}</p>
          </div>
        ))}
      </div>

      {/* Chi tiết chuyến đi (Bên phải) */}
      {selectedTrip && (
        <div className="trip-details">
          <h2>Chi tiết chuyến đi: {selectedTrip.name}</h2>
          <p>Số tiền đã chi: {selectedTrip.cost.toLocaleString()} VND</p>

          {/* Danh sách chi phí */}
          <h3>Danh sách chi phí:</h3>
          <ul>
            {expenses.map((expense, index) => (
              <li key={index}>
                <strong>{expense.category}</strong>: {expense.amount.toLocaleString()} VND
                {expense.description && <p>{expense.description}</p>}
              </li>
            ))}
          </ul>

          {/* Danh sách địa điểm đã đi */}
          <h3>Địa điểm đã đi:</h3>
          <ul>
            {selectedTrip.places.map((place, index) => (
              <li key={index}>{place.name}</li>
            ))}
          </ul>

          {/* Bản đồ OpenStreetMap với Leaflet */}
          <MapContainer center={[selectedTrip.places[0].lat, selectedTrip.places[0].lng]} zoom={13} style={{ height: "400px", width: "100%" }}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            {selectedTrip.places.map((place, index) => (
              <Marker key={index} position={[place.lat, place.lng]}>
                <Popup>{place.name}</Popup>
              </Marker>
            ))}
          </MapContainer>

          {/* Dự báo thời tiết */}
          <h3>Dự báo thời tiết:</h3>
          <ul>
            {weatherForecasts.map((forecast, index) => (
              <li key={index}>
                <strong>{forecast.forecastDate}</strong>: {forecast.temperature}°C, {forecast.weatherCondition}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default TripHistory;
