// src/pages/Home.tsx
import React, { useState } from 'react';
import '../styles/home.css';
import logo from '../assets/logo.png';

const Home: React.FC = () => {
  const [input, setInput] = useState('');
  const [isExpanded, setIsExpanded] = useState(false);

  const handleSubmit = () => {
    if (input.trim() === '') return;
    console.log('User message:', input);
    setInput('');
    setIsExpanded(false);
  };

  return (
    <div className="home">
      <img src={logo} alt="CheapTrip Background Logo" className="background-logo" />

      <div className="content">
        <h1 className="text-4xl font-bold text-white mb-4">Ch√†o m·ª´ng ƒë·∫øn v·ªõi CheapTrip üåç</h1>
        <p className="text-lg text-gray-200 mb-6">Tr√≤ chuy·ªán v·ªõi ChipChip ƒë·ªÉ l√™n l·ªãch tr√¨nh du l·ªãch üß≥</p>
      </div>

      {/* Thanh chat gi·ªëng ChatGPT */}
      <div className="chat-input-container">
        <textarea
          className={`chat-input ${isExpanded ? 'expanded' : ''}`}
          placeholder="H·ªèi ChipChip v·ªÅ chuy·∫øn ƒëi ti·∫øp theo c·ªßa b·∫°n..."
          rows={1}
          value={input}
          onChange={(e) => {
            setInput(e.target.value);
            setIsExpanded(e.target.value.trim() !== '');
          }}
          onKeyDown={(e) => {
            if (e.key === 'Enter' && !e.shiftKey) {
              e.preventDefault();
              handleSubmit();
            }
          }}
        />
        <button className="send-button" onClick={handleSubmit}>
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
            <path
              d="M12 4L20 12L12 20L10.5 18.5L16 13H4V11H16L10.5 5.5L12 4Z"
              fill="currentColor"
            />
          </svg>
        </button>
      </div>

      <div className="scroll-indicator">
        <svg width="24" height="24" fill="#fff">
          <path
            d="M12 5V19M12 19L5 12M12 19L19 12"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </div>
    </div>
  );
};

export default Home;
