// src/components/MapRoute.jsx
import React, { useEffect } from 'react';
import { MapContainer, TileLayer, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine';
import L from 'leaflet';

const Routing = ({ places }) => {
  const map = useMap();

  useEffect(() => {
    if (!map || places.length < 2) return;

    const control = L.Routing.control({
      waypoints: places.map((place) => L.latLng(place.lat, place.lng)),
      routeWhileDragging: false,
      show: false,
    }).addTo(map);

    return () => map.removeControl(control);
  }, [map, places]);

  return null;
};

const MapRoute = ({ places }) => {
  const defaultCenter = places[0] || { lat: 16.0471, lng: 108.2062 };

  return (
    <MapContainer
      center={defaultCenter}
      zoom={7}
      scrollWheelZoom={true}
      style={{ height: '500px', width: '100%' }}
      className="rounded shadow"
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      <Routing places={places} />
    </MapContainer>
  );
};

export default MapRoute;
