// Home.tsx
import React, { useState, useEffect, useRef } from 'react';
import '../styles/home.css';
import logo from '../assets/logo.png';
import { askGemini } from '../services/aiService';

const Home: React.FC = () => {
  const [messages, setMessages] = useState<any[]>([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLTextAreaElement>(null);
  const chatHistoryRef = useRef<HTMLDivElement>(null);

  const handleSend = async () => {
    if (!input.trim() || loading) return;

    const userMessage = { role: 'user', parts: [{ text: input }] };
    const systemPrompt = {
      role: 'user',
      parts: [
        {
          text: 'Bạn là một trợ lý ảo chuyên về du lịch tên là chipchip. Chỉ trả lời các câu hỏi liên quan đến thông tin khách hàng và du lịch như gợi ý hành trình, địa điểm tham quan, ngân sách, phương tiện di chuyển, trải nghiệm. Nếu câu hỏi không liên quan, hãy từ chối một cách lịch sự. Không hỏi nhiều hơn 1 câu. Thêm icon cuối câu'
        }
      ]
    };

    const conversation = [systemPrompt, ...messages, userMessage];
    setMessages((prev) => [...prev, userMessage]);
    setInput('');
    setLoading(true);

    try {
      const reply = await askGemini(conversation);
      const botMessage = { role: 'model', parts: [{ text: reply }] };
      setMessages((prev) => [...prev, botMessage]);
    } catch (err) {
      console.error(err);
      setMessages((prev) => [
        ...prev,
        { role: 'model', parts: [{ text: '❌ Lỗi kết nối đến AI' }] }
      ]);
    } finally {
      setLoading(false);
      setTimeout(() => inputRef.current?.focus(), 0);
    }
  };

  const renderText = (msg: any) => msg.parts?.[0]?.text || msg.text || '';

  useEffect(() => {
    if (chatEndRef.current && chatHistoryRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: 'smooth', block: 'end' });
    }
  }, [messages, loading]);

  return (
    <div className="home">
      <img src={logo} alt="CheapTrip Background Logo" className="background-logo" />

      <div className="content">
        <h1 className="slogan">CheapTrip gà mà cũng đi chơi!</h1>
      </div>

      <div className="chat-wrapper">
        <div className={`chat-box beautiful ${messages.length > 0 ? 'visible' : 'start-only'}`}>
          <div className="chat-history" ref={chatHistoryRef}>
            {messages.map((m, i) => (
              <div key={i} className={`chat-message ${m.role === 'user' ? 'user' : 'bot'}`}>
                <div className="message-content">{renderText(m)}</div>
              </div>
            ))}
            {loading && (
              <div className="chat-message bot">
                <div className="message-content">Đợi ChipChip xíuuu...</div>
              </div>
            )}
            <div ref={chatEndRef} />
          </div>

          <div className="chat-input-container beautiful">
            <textarea
              ref={inputRef}
              className="chat-input beautiful"
              placeholder="ChipChip nghe nè..."
              rows={1}
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  handleSend();
                }
              }}
              disabled={loading}
            />
            <button className="send-button beautiful" onClick={handleSend} disabled={loading}>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                <path
                  d="M12 4L20 12L12 20L10.5 18.5L16 13H4V11H16L10.5 5.5L12 4Z"
                  fill="currentColor"
                />
              </svg>
            </button>
          </div>
        </div>
      </div>

      <div className="scroll-indicator">
        <svg width="24" height="24" fill="#fff">
          <path
            d="M12 5V19M12 19L5 12M12 19L19 12"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />
        </svg>
      </div>
    </div>
  );
};

export default Home;
