import React, { useState } from 'react';
import { askGemini } from '../services/aiService';
import MapRoute from '../components/MapRoute';

const AssistantPage = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [places, setPlaces] = useState([]);

  const handleSend = async () => {
    if (!input.trim() || loading) return;

    const userMessage = { role: 'user', parts: [{ text: input }] };

    const systemPrompt = {
      role: 'user',
      parts: [{
        text: 'B·∫°n l√† m·ªôt tr·ª£ l√Ω ·∫£o chuy√™n v·ªÅ du l·ªãch t√™n l√† ChipChip. Ch·ªâ h·ªèi 1 c√¢u m·ªói l∆∞·ª£t. Ch·ªâ tr·∫£ l·ªùi v·ªÅ l·ªãch tr√¨nh, ƒë·ªãa ƒëi·ªÉm, ng√¢n s√°ch du l·ªãch. N·∫øu ng∆∞·ªùi d√πng h·ªèi ch·ªß ƒë·ªÅ kh√°c, t·ª´ ch·ªëi nh·∫π nh√†ng. Th√™m icon v√†o cu·ªëi c√¢u.'
      }]
    };

    const conversation = [systemPrompt, ...messages, userMessage];

    setMessages((prev) => [...prev, userMessage]);
    setInput('');
