    } finally {
      setLoading(false);
    }
  };

  const extractLocations = (text) => {
    // üß† B·∫°n c√≥ th·ªÉ thay logic n√†y b·∫±ng regex n√¢ng cao h∆°n
    const regex = /(?:t·∫°i|ƒë·∫øn|·ªü)\s([A-Zƒê][a-z√°√†·∫£√£·∫°√¢ƒÉ√©√®√™√≠√¨√Æ√≥√≤√¥∆°√∫√π∆∞√Ω·ª≥·ªπƒëA-Z ]+)/g;
    const results = [];
    let match;
    while ((match = regex.exec(text)) !== null) {
      const place = match[1].trim();
      if (place && !results.includes(place)) {
        results.push(place);
