import React, { useState } from 'react';
import '../styles/home.css';
import { askGemini } from '../services/aiService';

interface Message {
  role: 'user' | 'model';
  parts: { text: string }[];
}

const Home: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(false);

  const handleSend = async () => {
    if (!input.trim() || loading) return;

    const userMessage: Message = {
      role: 'user',
      parts: [{ text: input }],
    };

    const systemPrompt: Message = {
      role: 'user',
      parts: [{
        text: 'B·∫°n l√† m·ªôt tr·ª£ l√Ω ·∫£o chuy√™n v·ªÅ du l·ªãch t√™n l√† chipchip. Ch·ªâ tr·∫£ l·ªùi c√°c c√¢u h·ªèi li√™n quan ƒë·∫øn th√¥ng tin kh√°ch h√†ng v√† du l·ªãch nh∆∞ g·ª£i √Ω h√†nh tr√¨nh, ƒë·ªãa ƒëi·ªÉm tham quan, ng√¢n s√°ch, ph∆∞∆°ng ti·ªán di chuy·ªÉn, tr·∫£i nghi·ªám. N·∫øu c√¢u h·ªèi kh√¥ng li√™n quan, h√£y t·ª´ ch·ªëi m·ªôt c√°ch l·ªãch s·ª±. Kh√¥ng h·ªèi nhi·ªÅu h∆°n 1 c√¢u. Th√™m icon cu·ªëi c√¢u',
      }],
    };

    const conversation: Message[] = [systemPrompt, ...messages, userMessage];

    setMessages((prev) => [...prev, userMessage]);
    setInput('');
    setLoading(true);

    try {
      const reply = await askGemini(conversation);
      const botMessage: Message = {
        role: 'model',
        parts: [{ text: reply }],
      };
      setMessages((prev) => [...prev, botMessage]);
    } catch (err) {
      console.error(err);
      setMessages((prev) => [
        ...prev,
        {
          role: 'model',
          parts: [{ text: '‚ùå L·ªói k·∫øt n·ªëi ƒë·∫øn AI' }],
        },
      ]);
    } finally {
      setLoading(false);
    }
  };

  const renderText = (msg: Message): string => msg.parts?.[0]?.text || '';

  return (
    <div className="home flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="chat-container w-full max-w-2xl bg-white shadow-lg rounded-xl overflow-hidden">
        
        {/* Header - ƒëang tr√≤ chuy·ªán v·ªõi ChipChip */}
        <div className="chat-header bg-blue-600 text-white p-4 text-center text-lg font-semibold">
          ƒêang tr√≤ chuy·ªán v·ªõi <span className="font-bold">ChipChip üß≠</span>
        </div>

        {/* Chat Messages */}
        <div className="chat-messages h-96 overflow-y-auto p-4 bg-gray-50">
          {messages.map((m, i) => (
            <div key={i} className={`mb-3 flex ${m.role === 'user' ? 'justify-end' : 'justify-start'}`}>
              <div
                className={`max-w-xs p-3 rounded-xl text-sm shadow ${
                  m.role === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-black'
                }`}
              >
                {renderText(m)}
              </div>
            </div>
          ))}
          {loading && (
            <div className="mb-3 text-left">
              <span className="inline-block bg-white shadow p-2 rounded">
                ChipChip ƒëang suy nghƒ©...
              </span>
            </div>
          )}
        </div>

        {/* Input */}
        <div className="chat-input flex border-t border-gray-300">
          <input
            className="flex-grow p-3 outline-none text-sm"
            type="text"
            placeholder="H·ªèi ChipChip v·ªÅ chuy·∫øn ƒëi c·ªßa b·∫°n..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleSend()}
            disabled={loading}
          />
          <button
            onClick={handleSend}
            className="bg-blue-600 text-white px-4 text-sm hover:bg-blue-700 transition"
            disabled={loading}
          >
            G·ª≠i
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
